mapper.select:
SELECT * FROM exam.salary
salary.id = #{id}
AND month LIKE "%"#{month}"%" AND basic LIKE "%"#{basic}"%" AND allowance LIKE "%"#{allowance}"%" AND reward LIKE "%"#{reward}"%" AND insurance LIKE "%"#{insurance}"%" AND gongJiJin LIKE "%"#{gongJiJin}"%" AND money LIKE "%"#{money}"%" AND userId LIKE "%"#{userId}"%"
